import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import axios from "axios";
import { SERVER_HOST } from "../config/global_constants";
import { Form, Row, Col, Button, InputGroup } from "react-bootstrap";
import Swal from "sweetalert2";
import { XLg } from "react-bootstrap-icons";
import FileBase64 from "react-file-base64";
import AddMovie from "./AddMovie";

export default class AddMovieOption extends Component {
  constructor(props) {
    super(props);

    this.state = {
      title: "",
      year: "",
      runtime: "",
      selectedGenre: "",
      genres: [],
      director: "",
      actors: "",
      plot: "",
      posterUrl: "",
      dataset: "",
      redirectToHome: false,
    };
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  addGenre = (e) => {
    if (
      this.state.selectedGenre !== "" &&
      !this.state.genres.includes(this.state.selectedGenre)
    ) {
      this.setState({
        genres: [...this.state.genres, this.state.selectedGenre],
      });
    }
  };

  removeGenre = (e) => {
    this.setState({
      genres: this.state.genres.filter((g) => g !== e.currentTarget.dataset.id),
    });
  };

  uploadPhoto(base64) {
    this.setState({ posterUrl: base64 });
  }

  handleSubmit = (e) => {
    e.preventDefault();

    const movieObject = {
      title: this.state.title,
      year: this.state.year,
      runtime: this.state.runtime,
      genres: this.state.genres,
      director: this.state.director,
      actors: this.state.actors,
      plot: this.state.plot,
      posterUrl: this.state.posterUrl,
    };

    axios.post(`${SERVER_HOST}/movies`, movieObject).then((res) => {
      if (res.data) {
        if (res.data.errorMessage) {
          console.log(res.data.errorMessage);
        } else {
          console.log("Record added");
          this.setState({ redirectToHome: true });
        }
        Swal.fire({
          title: "Record Added",
          text: "The movie has been added.",
          icon: "success",
          showConfirmButton: false,
          timer: 1500,
        });
      } else {
        console.log("Record not added");
      }
    });
  };

  handleSubmitDataset = (e) => {
    e.preventDefault();

    const movieArray = JSON.parse(this.state.dataset);

    axios.post(`${SERVER_HOST}/movies`, movieArray).then((res) => {
      if (res.data) {
        if (res.data.errorMessage) {
          console.log(res.data.errorMessage);
        } else {
          console.log("Records added");
          this.setState({ redirectToHome: true });
        }
        Swal.fire({
          title: "Records Added",
          text: "Movie dataset has been added.",
          icon: "success",
          showConfirmButton: false,
          timer: 1500,
        });
      } else {
        console.log("Records not added");
      }
    });
  };

  render() {
    return (
      <div id="form">
        <h2>Add New Movie</h2>
        {this.state.redirectToHome ? <Redirect to="/Home" /> : null}

        <AddMovie
          title={this.state.title}
          year={this.state.year}
          runtime={this.state.runtime}
          genres={this.state.genres}
          director={this.state.director}
          actors={this.state.actors}
          plot={this.state.plot}
          posterUrl={this.state.posterUrl}
          onChange={this.handleChange}
          onSubmit={this.handleSubmit}
          addGenre={this.addGenre}
          removeGenre={this.removeGenre}
          uploadPhoto={this.uploadPhoto}
        />

        <h2 className="mt-5">Add Movie Dataset</h2>
        <div className="form-container">
          <Form>
            <Form.Group>
              <Form.Label>Add Movie Dataset</Form.Label>
              <Form.Control
                type="text"
                name="dataset"
                value={this.state.dataset}
                onChange={this.handleChange}
                as="textarea"
                style={{ height: "500px" }}
              />
            </Form.Group>
            <button
              className="btn btn-success mr-2"
              onClick={this.handleSubmitDataset}
            >
              Add
            </button>
            <Link className="btn btn-danger ml-2" to={"/Home"}>
              Cancel
            </Link>
          </Form>
        </div>
      </div>
    );
  }
}
