import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import axios from "axios";
import { SERVER_HOST } from "../config/global_constants";
import { Form, Row, Col, Button, InputGroup } from "react-bootstrap";
import Swal from "sweetalert2";

export default class AddMovieOption extends Component {
  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  addGenre = (e) => {
    if (
      this.state.selectedGenre !== "" &&
      !this.state.genres.includes(this.state.selectedGenre)
    ) {
      this.setState({
        genres: [...this.state.genres, this.state.selectedGenre],
      });
    }
  };

  removeGenre = (e) => {
    this.setState({
      genres: this.state.genres.filter((g) => g !== e.currentTarget.dataset.id),
    });
  };

  handleSubmitDataset = (e) => {
    e.preventDefault();

    const movieArray = JSON.parse(this.state.dataset);

    axios.post(`${SERVER_HOST}/movies`, movieArray).then((res) => {
      if (res.data) {
        if (res.data.errorMessage) {
          console.log(res.data.errorMessage);
        } else {
          console.log("Records added");
          this.setState({ redirectToHome: true });
        }
        Swal.fire({
          title: "Records Added",
          text: "Movie dataset has been added.",
          icon: "success",
          showConfirmButton: false,
          timer: 1500,
        });
      } else {
        console.log("Records not added");
      }
    });
  };

  render() {
    return (
      <div id="form">
        <ul>
          <li>Add one movie</li>
          <li>Add movie dataset</li>
        </ul>

        {this.state.redirectToHome ? <Redirect to="/Home" /> : null}

        <h2 className="mt-5">Add Movie Dataset</h2>
        <div className="form-container">
          <Form>
            <Form.Group>
              <Form.Label>Add Movie Dataset</Form.Label>
              <Form.Control
                type="text"
                name="dataset"
                value={this.state.dataset}
                onChange={this.handleChange}
                as="textarea"
                style={{ height: "500px" }}
              />
            </Form.Group>
            <button
              className="btn btn-success mr-2"
              onClick={this.handleSubmitDataset}
            >
              Add
            </button>
          </Form>
        </div>
      </div>
    );
  }
}
