import React, { Component } from "react";
import { Link } from "react-router-dom";
import MoviesBox from "./MoviesBox";
import Swal from "sweetalert2";

import axios from "axios";

import { SERVER_HOST } from "../config/global_constants";

export default class DisplayAllMovies extends Component {
  constructor(props) {
    super(props);

    this.state = {
      movies: [],
    };
  }

  addOption() {
    Swal.fire({
      title: "Do you want to save the changes?",
      showDenyButton: true,
      showCancelButton: true,
      confirmButtonText: "Save",
      denyButtonText: `Don't save`,
    }).then((result) => {
      /* Read more about isConfirmed, isDenied below */
      if (result.isConfirmed) {
        Swal.fire("Saved!", "", "success");
      } else if (result.isDenied) {
        Swal.fire("Changes are not saved", "", "info");
      }
    });
  }

  componentDidMount() {
    axios.get(`${SERVER_HOST}/movies`).then((res) => {
      if (res.data) {
        if (res.data.errorMessage) {
          console.log(res.data.errorMessage);
        } else {
          console.log("Records read");
          this.setState({ movies: res.data });
        }
      } else {
        console.log("Record not found");
      }
    });
  }

  render() {
    return (
      <div className="container movies-container">
        <div className="movie-cont-head">
          <h2>All Movies</h2>
          <button className="btn btn-light" onClick={this.addOption}>
            Add New Movie
          </button>
        </div>

        <div className="row icon-boxes">
          {this.state.movies.map((movie) => (
            <MoviesBox key={movie._id} movie={movie} />
          ))}
        </div>
      </div>
    );
  }
}
