import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import axios from "axios";
import { Form } from "react-bootstrap";
import { SERVER_HOST } from "../config/global_constants";
import Swal from "sweetalert2";

export default class AddMovieDataset extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirectToHome: false,
      dataset: "",
    };
  }
  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmitDataset = (e) => {
    e.preventDefault();

    const movieArray = JSON.parse(this.state.dataset);

    axios.post(`${SERVER_HOST}/movies`, movieArray).then((res) => {
      if (res.data) {
        if (res.data.errorMessage) {
          console.log(res.data.errorMessage);
        } else {
          console.log("Records added");
          this.setState({ redirectToHome: true });
        }
        Swal.fire({
          title: "Records Added",
          text: "Movie dataset has been added.",
          icon: "success",
          showConfirmButton: false,
          timer: 1500,
        });
      } else {
        console.log("Records not added");
      }
    });
  };

  render() {
    return (
      <div id="form">
        <div className="text-align-center">
          <Link className="btn " to={"/AddMovie"}>
            Add one movie
          </Link>
          <Link className="btn " to={"/AddMovieDataset"}>
            Add movie dataset
          </Link>
        </div>
        {this.state.redirectToHome ? <Redirect to="/Home" /> : null}
        <h2>Add Movie Dataset</h2>
        <div className="form-container">
          <Form>
            <Form.Group>
              <Form.Label>Dataset</Form.Label>
              <Form.Control
                type="text"
                name="dataset"
                value={this.state.dataset}
                onChange={this.handleChange}
                as="textarea"
                style={{ height: "500px" }}
              />
            </Form.Group>
            <button
              className="btn btn-success mr-2"
              onClick={this.handleSubmitDataset}
            >
              Add
            </button>
            <Link className="btn btn-danger ml-2" to={"/Home"}>
              Cancel
            </Link>
          </Form>
        </div>
      </div>
    );
  }
}
