import React, { Component } from "react";
import { Form } from "react-bootstrap";
import AddMovie from "./AddMovie";
import AddMovieDataset from "./AddMovieDataset";

export default class AddMovieOption extends Component {
  render() {
    return (
      <div id="form">
        <ul>
          <li>Add one movie</li>
          <li>Add movie dataset</li>
        </ul>

        <AddMovie />

        <h2 className="mt-5">Add Movie Dataset</h2>
        <div className="form-container">
          <Form>
            <Form.Group>
              <Form.Label>Add Movie Dataset</Form.Label>
              <Form.Control
                type="text"
                name="dataset"
                value={this.state.dataset}
                onChange={this.handleChange}
                as="textarea"
                style={{ height: "500px" }}
              />
            </Form.Group>
            <button
              className="btn btn-success mr-2"
              onClick={this.handleSubmitDataset}
            >
              Add
            </button>
          </Form>
        </div>
      </div>
    );
  }
}
